import React from "react";
import { useContext } from "react";
import { RoomContext } from "../context";
import Title from "./title";

// GET UNIQUE VALUES
const getUnique = (items, values) => {
  return [...new Set(items.map(item => item[values]))];
};

//USING HOOKS WITH REACT
const RoomFilter = () => {
  const mycontext = useContext(RoomContext);

  const {
    handleChange,
    type,
    capacity,
    price,
    minPrice,
    maxPrice,
    minSize,
    maxSize,
    breakfast,
    pets
  } = mycontext;

  // GETING UNIQUE TYPES
  let types = getUnique(mycontext.rooms, "type");
  //ADD ALL
  types = ["all", ...types];

  //jsx maping
  let mytypes = types.map((myitem, index) => {
    return (
      <option value={myitem} key={index}>
        {myitem}
      </option>
    );
  });

  // GETING UNIQUE GUESTS
  let people = getUnique(mycontext.rooms, "capacity");

  people = people.map((mypeople, index) => {
    return (
      <option value={mypeople} key={index}>
        {mypeople}
      </option>
    );
  });

  return (
    <section className="filter-container">
      <Title title="search rooms" />
      <form className="filter-form">
        
        <div className="col-md-2">
          <div className="form-group">
            {/* START OF SELECT*/}
            <label htmlFor="type" className="label">
              select
            </label>
            <select
              name="type"
              id="type"
              value={type}
              onChange={handleChange}
              className="form-control"
            >
              {mytypes}
            </select>
            {/* END OF SELECT */}
          </div>
        </div>
        <div className="form-group">
          {/* START OF GUEST*/}
          <label htmlFor="capacity" className="label">
            guest
          </label>
          <select
            name="capacity"
            id="capacity"
            value={type}
            onChange={handleChange}
            className="form-control"
          >
            {people}
          </select>
          {/* END OF GUET */}
        </div>
        <div className="from-group">
          {/* PRICE SECTION */}
          <label htmlFor="price" className="label">
            price ${price}
          </label>
          <input
            type="range"
            name="price"
            id="price"
            value={price}
            max={maxPrice}
            min={minPrice}
            onChange={handleChange}
            className="form-control"
          />
          {/* END OF PRICE SECTION */}
        </div>
        {/* ROOM SIZE */}
        <div className="form-group">
          <label htmlFor="size">room size</label>
          <div className="form-group">
            <label htmlFor="size">min size (squr)</label>
            <input
              type="number"
              name="minSize"
              id="size"
              value={minSize}
              onChange={handleChange}
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label htmlFor="size">max size (squr)</label>
            <input
              type="number"
              name="maxSize"
              id="size"
              value={maxSize}
              onChange={handleChange}
              className="form-control"
            />
          </div>
        </div>
        {/* END OF ROOM SIZE */}
        {/* START OF EXTRAS */}
        <div className="form-group">
          <label htmlFor="extras">extras</label>
          <div className="form-group">
            <label htmlFor="breakfast">breakfast</label>
            <input
              type="checkbox"
              name="breakfast"
              id="breakfast"
              checked={breakfast}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="pets">pet</label>
            <input
              type="checkbox"
              name="pets"
              id="pets"
              checked={pets}
              onChange={handleChange}
            />
          </div>
        </div>
        {/* END OF EXTRAS */}
      </form>
    </section>
  );
};

export default RoomFilter;
