import React from "react";
import { useContext } from "react";
import { RoomContext } from "../context";
import Title from "./title";

// GET UNIQUE VALUES
const getUnique = (items, values) => {
  return [...new Set(items.map(item => item[values]))];
};

//USING HOOKS WITH REACT
const RoomFilter = () => {
  const mycontext = useContext(RoomContext);

  const {
    handleChange,
    type,
    capacity,
    price,
    minPrice,
    maxPrice,
    minSize,
    maxSize,
    breakfast,
    pets
  } = mycontext;

  // GETING UNIQUE TYPES
  let types = getUnique(mycontext.rooms, "type");
  //ADD ALL 
  types = ['all', ...types];
  console.log(types);
  //jsx maping
  

  return (
    <section className="filter-container">
      <Title title="search rooms" />
      <form className="filter-form">
        <div className="form-group">
          {/* START OF SELECT*/}
          <label htmlFor="type">select</label>
          <select
            name="type"
            id="type"
            value={type}
            onChange={handleChange}
            className="form-control"
          />
          {/* END OF SELECT */}
        </div>
      </form>
    </section>
  );
};

export default RoomFilter;
